package org.venustus.algorithms.strings

import org.scalatest.{FlatSpec, Matchers}

/**
 * Created by venkat on 20/05/15.
 */
class SubstringDiffSpec extends FlatSpec with Matchers {

    "Substring diff function" should "give correct substring diff with brute force algorithm" in {
        SubstringDiff.findMaxSubstringWithLimitedDiffBruteForce("tabriz", "torino", 2) should be (4)
        SubstringDiff.findMaxSubstringWithLimitedDiffBruteForce("abacba", "abcaba", 0) should be (3)
        SubstringDiff.findMaxSubstringWithLimitedDiffBruteForce("helloworld", "yellomarin", 3) should be (8)
    }

    "Substring diff function" should "give correct substring diff" in {
        SubstringDiff.findMaxSubstringWithLimitedDiffEfficient("tabriz", "torino", 2) should be (4)
        SubstringDiff.findMaxSubstringWithLimitedDiffEfficient("abacba", "abcaba", 0) should be (3)
        SubstringDiff.findMaxSubstringWithLimitedDiffEfficient("helloworld", "yellomarin", 3) should be (8)
        //SubstringDiff.findMaxSubstringWithLimitedDiffEfficient("","", 11) should be (256)
    }

}
