package org.venustus.algorithms.strings

import org.scalatest.{FlatSpec, Matchers}

/**
 * Created by venkat on 13/07/15.
 */
class ValidStringSpec  extends FlatSpec with Matchers {

    "Algorithm to detect valid strings" should "detect correctly" in {
        ValidString.isValidString("hfchdkkbfifgbgebfaahijchgeeeiagkadjfcbekbdaifchkjfejckbiiihegacfbchdihkgbkbddgaefhkdgccjejjaajgijdkd") should be (true)
        ValidString.isValidString("") should be (true)
    }

}
